package main

import (
	"fmt"
	"strconv"

	"github.com/F-r-o-i-d/GoExF/GoExF/GoSploit/InfectApp"
)

func stringToBin(s string) (binString string) {
	for _, c := range s {
		binString = fmt.Sprintf("%s%b", binString, c)
	}
	return
}

func binToString(b string) (string, error) {
	var out string
	for i := 0; i < len(b); i += 8 {
		bb := b[i : i+8]
		n, err := strconv.ParseInt(bb, 2, 64)
		if err != nil {
			return "", err
		}
		out += string(n)
	}
	return out, nil
}

